import styled from "styled-components";
import PowerButton from './PowerButton';
import LogoComponent from './LogoComponent';
import SocialIcons from './SocialIcons';
import { NavLink } from 'react-router-dom';


const MainContainer = styled.main`
  background: ${(props) => props.theme.body};
  width: 100vw;
  height: 100vh;
  overflow: hidden;
  position: relative;

  h2,h3, h4, h5, h6 {
    font-family: 'karla', sans-serif;
    font-weight: 500;
  }
`;

const Container = styled.div`
  padding: 2rem;
`

const Contact = styled(NavLink)`
  color: ${props => props.theme.text};
  position: absolute;
  top: 2rem;
  right: calc(1rem + 2vw);
  text-decoration: none;
  z-index: 1;
`

const Blog = styled(NavLink)`
  color: ${props => props.theme.text};
  position: absolute;
  top: 50%;
  right: calc(1rem + 2vw);
  transform: rotate(-90deg); translate(-50%, -50%)
  text-decoration: none;
  z-index: 1;
`

const Work = styled(NavLink)`
  color: ${props => props.theme.text};
  position: absolute;
  top: 50%;
  left: 2rem;
  transform: rotate(90deg); translate(-50%, -50%)
  text-decoration: none;
  z-index: 1;
`

const Home = () => {
  return <>
    <MainContainer>
      <Container>
        <PowerButton />
        <LogoComponent />
        <SocialIcons />

        <Contact target='_blank' to={{pathname: 'mailto:alifikripr@gmail.com'}}>
          <h3>
            say hi...
          </h3>
        </Contact>

        <Blog to='/blog'>
          <h3>
            Blog
          </h3>
        </Blog>

        <Work to='/work'>
          <h3>
            Work
          </h3>
        </Work>
      </Container>
    </MainContainer>
   </>;
};

export default Home;
